  def test_sIPCriarMontarOrgao(self):
    self.driver.get(os.environ["base_url"]+"/sip/login.php?sigla_orgao_sistema="+os.environ["selOrgao"]+"&sigla_sistema=SIP")
    self.driver.find_element(By.ID, "txtUsuario").send_keys("teste")
    self.driver.find_element(By.ID, "pwdSenha").click()
    self.driver.find_element(By.ID, "pwdSenha").send_keys("teste")
    self.driver.find_element(By.ID, "Acessar").click()
    self.driver.find_element(By.XPATH, "//ul[@id=\'infraMenu\']/li/a/span[contains(.,\'Órgãos\')]").click()
    self.driver.find_element(By.LINK_TEXT, "Novo").click()
    WebDriverWait(self.driver, 30).until(expected_conditions.element_to_be_clickable((By.ID, "txtSigla")))
    self.driver.find_element(By.ID, "txtSigla").click()
    self.driver.find_element(By.ID, "txtSigla").send_keys("Org2")
    self.driver.find_element(By.ID, "txtDescricao").click()
    self.driver.find_element(By.ID, "txtDescricao").send_keys("Orgao 2")
    self.driver.find_element(By.ID, "txtOrdem").click()
    self.driver.find_element(By.ID, "txtOrdem").send_keys("1")
    self.driver.find_element(By.NAME, "sbmCadastrarOrgao").click()
    WebDriverWait(self.driver, 30).until(expected_conditions.presence_of_element_located((By.ID, "divInfraAreaTelaD")))
    self.driver.find_element(By.ID, "divInfraAreaTelaD").click()
    assert self.driver.find_element(By.ID, "divInfraBarraLocalizacao").text == "Órgãos"
  
